sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	// implement resource here
	try{

		if(request.queryParams.n >= 0)
		{
			var n1=0,n2=1,n3,i,count=request.queryParams.n;
			var fibSeries = n1 + " " + n2 + " ";
			for(i=2;i<count;++i)//loop starts from 2 because 0 and 1 are already printed    
			{    
				n3=n1+n2;    
				fibSeries += n3 + " ";    
				n1=n2;    
				n2=n3;    
			}
			return fibSeries;
		}
		else
		{
			var myError = new sn_ws_err.ServiceError();
			myError.setStatus(500);
			myError.setMessage('Negative Numbers are not allowed.');
			myError.setDetail('Please Enter a number which is not negative or less than 0.');
			return myError;
		}

	}
	catch(e)
	{
		gs.log("Exception Found in Project Fibo "+e);
	}

})(request, response);
